RICH_AVAILABLE = False
_console_obj = None

def _pip_install_user(pkg: str) -> bool:
    try:
        subprocess.check_call([sys.executable, "-m", "pip", "install", "--user", pkg])
        return True
    except Exception:
        return False

def init_rich() -> None:
    global RICH_AVAILABLE
    try:
        import rich  # noqa: F401
        RICH_AVAILABLE = True
        return
    except Exception:
        pass
    print("[INFO] Installing optional dependency: rich (no root) ...")
    if _pip_install_user("rich"):
        try:
            import rich  # noqa: F401
            RICH_AVAILABLE = True
            return
        except Exception:
            pass
    print("[WARN] rich not available. Using plain mode.")
    RICH_AVAILABLE = False

init_rich()

def _console():
    global _console_obj
    if _console_obj is None:
        if RICH_AVAILABLE:
            from rich.console import Console
            _console_obj = Console()
        else:
            class Dummy:
                def print(self, *a, **k): print(*a)
            _console_obj = Dummy()
    return _console_obj

def cprint(*args, **kwargs) -> None:
    if RICH_AVAILABLE:
        _console().print(*args, **kwargs)
    else:
        print(*args)

def prompt(text: str, default: Optional[str] = None) -> str:
    if RICH_AVAILABLE:
        from rich.prompt import Prompt
        return Prompt.ask(str(text), default=default) if default is not None else Prompt.ask(str(text))
    else:
        if default is None:
            return input(str(text) + ": ").strip()
        ans = input(str(text) + " [" + str(default) + "]: ").strip()
        return ans if ans else str(default)

