def run_python(code: str, *, safe_mode: bool = False, timeout_sec: int = 3):
    """Execute Python code for local learning.

    - Captures stdout/stderr.
    - Optional *safe_mode* blocks imports and gives a small builtins set.
    - Optional *timeout_sec* tries to stop infinite loops (SIGALRM when available).
    """
    import io, contextlib, builtins, signal

    class _Timeout(Exception):
        pass

    ns: Dict[str, Any] = {}

    if safe_mode:
        # Minimal builtins for beginner-friendly practice.
        allowed = {
            "print": print,
            "range": range,
            "len": len,
            "int": int,
            "float": float,
            "str": str,
            "bool": bool,
            "list": list,
            "dict": dict,
            "set": set,
            "tuple": tuple,
            "enumerate": enumerate,
            "sum": sum,
            "min": min,
            "max": max,
            "abs": abs,
            "sorted": sorted,
            "reversed": reversed,
            "zip": zip,
        }

        def _blocked_import(*_a, **_k):
            raise ImportError("Imports are disabled in Safe Mode. Disable Safe Mode in Settings if you need imports.")

        allowed["__import__"] = _blocked_import
        ns["__builtins__"] = allowed
    else:
        ns["__builtins__"] = builtins.__dict__

    out = io.StringIO()
    err = io.StringIO()

    def _alarm_handler(_signum, _frame):
        raise _Timeout(f"Execution timed out after {timeout_sec}s")

    had_alarm = hasattr(signal, "SIGALRM")
    prev_handler = None
    try:
        if timeout_sec and had_alarm:
            prev_handler = signal.getsignal(signal.SIGALRM)
            signal.signal(signal.SIGALRM, _alarm_handler)
            signal.alarm(int(timeout_sec))

        with contextlib.redirect_stdout(out), contextlib.redirect_stderr(err):
            exec(code, ns, ns)

    except _Timeout as e:
        err.write("TimeoutError: " + str(e) + "\n")
    except Exception as e:
        err.write("Exception: " + str(e) + "\n")
    finally:
        if timeout_sec and had_alarm:
            try:
                signal.alarm(0)
                if prev_handler is not None:
                    signal.signal(signal.SIGALRM, prev_handler)
            except Exception:
                pass

    return out.getvalue(), err.getvalue(), ns

def check_rules(rules: Dict[str, Any], ns: Dict[str, Any], stdout: str):
    rules = rules or {}
    for name in rules.get("must_define", []):
        if name not in ns:
            return False, "Missing variable: " + str(name)
    for fn in rules.get("must_define_func", []):
        if fn not in ns or not callable(ns[fn]):
            return False, "Missing function: " + str(fn)
    for s in rules.get("must_print_contains", []):
        if s and s not in stdout:
            return False, "Output must contain: " + str(s)
    ret = rules.get("must_return")
    if ret:
        fn = ret.get("func")
        args = ret.get("args", [])
        expected = ret.get("equals")
        if fn not in ns or not callable(ns[fn]):
            return False, "Missing function: " + str(fn)
        try:
            got = ns[fn](*args)
        except Exception as e:
            return False, "Error calling " + str(fn) + ": " + str(e)
        if got != expected:
            return False, str(fn) + str(tuple(args)) + " returned " + str(got) + ", expected " + str(expected)
    return True, "ok"

# ============================================================
# Special lesson helpers (guided sections)
# ============================================================

SECTION_MARKER_PREFIX = "<<<"
SECTION_MARKER_SUFFIX = ">>>"

def split_guided_sections(body: str) -> List[Tuple[str, str]]:
    """
    Split lesson body into guided sections if it contains markers like:
        <<<Section title>>>
    Returns list of (title, content). If no markers, returns [].
    """
    if SECTION_MARKER_PREFIX not in body:
        return []
    sections: List[Tuple[str, str]] = []
    current_title = ""
    current_lines: List[str] = []
    started = False
    for line in body.splitlines():
        s = line.strip()
        if s.startswith(SECTION_MARKER_PREFIX) and s.endswith(SECTION_MARKER_SUFFIX) and len(s) > 6:
            # Flush previous
            if started:
                sections.append((current_title or "Lesson", "\n".join(current_lines).strip()))
            started = True
            current_title = s[len(SECTION_MARKER_PREFIX):-len(SECTION_MARKER_SUFFIX)].strip()
            current_lines = []
        else:
            if started:
                current_lines.append(line)
    if started:
        sections.append((current_title or "Lesson", "\n".join(current_lines).strip()))
    # Consider it valid only if we have 2+ sections with non-empty content
    sections = [(t, c) for (t, c) in sections if c.strip()]
    return sections if len(sections) >= 2 else []

