CSS_PROJECTS: List[Project] = []
BASH_PROJECTS: List[Project] = []

def build_all_content() -> None:
    lid = 1
    for ch_en, ch_el, lessons in list(PY_TOPIC_BANK) + list(PY_SPECIAL_BANK):
        for (t_en, t_el, lvl, b_en, b_el, exs, ex) in lessons:
            ex_en, ex_el, rules, sol, cards = ex
            PYTHON_LESSONS.append(Lesson(
                id=lid,
                chapter_en=ch_en, chapter_el=ch_el,
                title_en=t_en, title_el=t_el,
                level=lvl,
                body_en=b_en, body_el=b_el,
                examples=exs,
                exercise=Exercise(ex_en, ex_el, rules, sol),
                flashcards=cards
            ))
            lid += 1

    def add_bank(bank, bucket):
        i = 1
        for ch_en, ch_el, items in bank:
            for (t_en, t_el, b_en, b_el, exs, ex_en, ex_el, sol_en, sol_el) in items:
                bucket.append(SimpleLesson(
                    id=i,
                    chapter_en=ch_en, chapter_el=ch_el,
                    title_en=t_en, title_el=t_el,
                    body_en=b_en, body_el=b_el,
                    examples=exs,
                    exercise_en=ex_en, exercise_el=ex_el,
                    solution_en=sol_en, solution_el=sol_el
                ))
                i += 1

    add_bank(JS_BANK, JS_LESSONS)
    add_bank(HTML_BANK, HTML_LESSONS)
    add_bank(CSS_BANK, CSS_LESSONS)
    add_bank(BASH_BANK, BASH_LESSONS)

    for name, en, el, hi_en, hi_el, examples in STDLIB_ENTRIES:
        STDLIB.append(StdlibEntry(
            name=name,
            summary_en=en,
            summary_el=el,
            highlights_en=hi_en,
            highlights_el=hi_el,
            examples=examples
        ))

    def build_projects(raw, bucket):
        for pr in raw:
            bucket.append(Project(
                id=pr[0], title_en=pr[1], title_el=pr[2], difficulty=pr[3],
                goal_en=pr[4], goal_el=pr[5],
                steps_en=pr[6], steps_el=pr[7],
                stretch_en=pr[8], stretch_el=pr[9]
            ))

    build_projects(PY_PROJECTS_RAW, PY_PROJECTS)
    build_projects(JS_PROJECTS_RAW, JS_PROJECTS)
    build_projects(HTML_PROJECTS_RAW, HTML_PROJECTS)
    build_projects(CSS_PROJECTS_RAW, CSS_PROJECTS)
    build_projects(BASH_PROJECTS_RAW, BASH_PROJECTS)

build_all_content()

