def _iter_notes_files() -> List[Path]:
    ensure_dirs()
    if not NOTES_DIR.exists():
        return []
    return sorted([p for p in NOTES_DIR.glob("*.txt") if p.is_file()], key=lambda x: x.name)

def notes_hub(p: Progress) -> None:
    lang = p.ui_lang
    while True:
        files = _iter_notes_files()
        cprint()
        cprint("[" + tr("notes_hub", lang) + "] — " + tr("made_by", lang))
        cprint("1) " + tr("list_notes", lang) + f" ({len(files)})")
        cprint("2) " + tr("search_notes", lang))
        cprint("3) " + tr("export_notes", lang))
        cprint("4) " + tr("delete_note", lang))
        cprint("5) " + tr("back", lang))
        sel = prompt(tr("select", lang), default="5").strip()

        if sel == "1":
            cprint()
            if not files:
                cprint("No notes yet." if lang == LANG_EN else "Δεν υπάρχουν σημειώσεις ακόμα.")
                continue
            for fp in files[:400]:
                cprint("- " + fp.name)
        elif sel == "2":
            kw = prompt(tr("keyword", lang), default="").strip()
            if not kw:
                continue
            k = kw.lower()
            hits = []
            for fp in files:
                try:
                    txt = fp.read_text(encoding="utf-8", errors="ignore")
                except Exception:
                    continue
                if k in txt.lower() or k in fp.name.lower():
                    hits.append(fp)
            cprint()
            if not hits:
                cprint(tr("not_found", lang))
            else:
                cprint(("Matches:" if lang == LANG_EN else "Ταιριάσματα:") + " " + str(len(hits)))
                for fp in hits[:200]:
                    cprint("- " + fp.name)
        elif sel == "3":
            ensure_dirs()
            ts = time.strftime("%Y%m%d_%H%M%S")
            outp = DATA_DIR / ("notes_export_" + ts + ".md")
            lines = []
            lines.append("# Notes export (" + ts + ")\n")
            for fp in files:
                lines.append("## " + fp.stem + "\n")
                try:
                    lines.append(fp.read_text(encoding="utf-8", errors="ignore").rstrip() + "\n")
                except Exception:
                    lines.append("[Could not read]\n")
            outp.write_text("\n".join(lines), encoding="utf-8")
            cprint((tr("saved", lang) + " " + str(outp)))
        elif sel == "4":
            name = prompt("Filename (e.g. python_lesson_1.txt)" if lang == LANG_EN else "Όνομα αρχείου (π.χ. python_lesson_1.txt)", default="").strip()
            if not name:
                continue
            fp = NOTES_DIR / name
            if fp.exists():
                confirm = prompt((tr("confirm", lang) + " delete? " + tr("yes_no", lang)) if lang == LANG_EN else (tr("confirm", lang) + " διαγραφή; " + tr("yes_no", lang)), default="n").lower()
                if confirm.startswith("y"):
                    try:
                        fp.unlink()
                        cprint(tr("saved", lang))
                    except Exception as e:
                        cprint("Error: " + str(e))
            else:
                cprint(tr("not_found", lang))
        elif sel == "5":
            return

def _zip_dir(src_dir: Path, dst_zip: Path) -> None:
    import zipfile
    with zipfile.ZipFile(dst_zip, "w", compression=zipfile.ZIP_DEFLATED) as z:
        for root, _dirs, files in os.walk(src_dir):
            for fn in files:
                p = Path(root) / fn
                rel = p.relative_to(src_dir)
                z.write(p, arcname=str(rel))

def settings_menu(p: Progress) -> None:
    lang = p.ui_lang
    while True:
        cprint()
        cprint("[" + tr("settings", lang) + "] — " + tr("made_by", lang))
        cprint(("• " + tr("safe_mode_on", lang)) if p.safe_mode else ("• " + tr("safe_mode_off", lang)))
