        cprint("4) " + tr("restore_data", lang))
        cprint("5) " + tr("reset_data", lang))
        cprint("6) " + tr("back", lang))
        sel = prompt(tr("select", lang), default="6").strip()

        if sel == "1":
            p.safe_mode = not bool(p.safe_mode)
            save_progress(p)
        elif sel == "2":
            ans = prompt(tr("wrap_width", lang), default=str(p.wrap_width)).strip()
            try:
                w = int(ans)
                if 50 <= w <= 140:
                    p.wrap_width = w
                    save_progress(p)
            except Exception:
                pass
        elif sel == "3":
            ensure_dirs()
            ts = time.strftime("%Y%m%d_%H%M%S")
            dst = BASE_DIR / ("learn_programming_backup_" + ts + ".zip")
            try:
                _zip_dir(DATA_DIR, dst)
                cprint(tr("saved", lang) + " " + str(dst))
            except Exception as e:
                cprint("Error: " + str(e))
        elif sel == "4":
            import zipfile, shutil
            zpath = prompt(tr("path", lang) + " (.zip)", default="").strip()
            if not zpath:
                continue
            zp = Path(zpath).expanduser()
            if not zp.exists():
                cprint(tr("not_found", lang))
                continue
            try:
                ensure_dirs()
                # Keep a safety copy
                safety = BASE_DIR / ("learn_programming_data_before_restore_" + time.strftime("%Y%m%d_%H%M%S") + ".zip")
                try:
                    _zip_dir(DATA_DIR, safety)
                except Exception:
                    pass
                # Restore
                shutil.rmtree(DATA_DIR, ignore_errors=True)
                ensure_dirs()
                with zipfile.ZipFile(zp, "r") as z:
                    z.extractall(DATA_DIR)
                cprint(tr("saved", lang))
            except Exception as e:
                cprint("Error: " + str(e))
        elif sel == "5":
            import shutil
            confirm = prompt((tr("confirm", lang) + " reset? " + tr("yes_no", lang)) if lang == LANG_EN else (tr("confirm", lang) + " μηδενισμός; " + tr("yes_no", lang)), default="n").lower()
