def _path_filtered_python_lessons(p: Progress) -> List[Lesson]:
    key = getattr(p, "learning_path", "guided_beginner")
    path = next((x for x in PY_LEARNING_PATHS if x["key"] == key), None)
    if not path:
        return PYTHON_LESSONS
    prefix = path.get("chapter_prefix_en", "")
    if not prefix:
        return PYTHON_LESSONS
    return [l for l in PYTHON_LESSONS if l.chapter_en.startswith(prefix)]

def quick_quiz(p: Progress) -> None:
    """A lightweight quiz mode using existing Python SRS cards."""
    lang = p.ui_lang
    srs = load_srs()
    cards = srs.get("python", [])
    if not cards:
        cprint("No cards yet. Open a Python lesson first to auto-add flashcards." if lang == LANG_EN else "Δεν υπάρχουν κάρτες ακόμα. Άνοιξε πρώτα ένα μάθημα Python για να προστεθούν.")
        return
    today = today_str()
    due = [c for c in cards if c.get("due", today) <= today]
    pool = due if due else cards
    random.shuffle(pool)
    cprint()
    cprint("[" + tr("quiz", lang) + "] — Python — " + tr("made_by", lang))
    n = min(12, len(pool))
    for card in pool[:n]:
        input("Q: " + card["q"] + "\n(Enter)")
        cprint("A: " + card["a"])
        rating = prompt("Rate: 1=again 2=hard 3=good 4=easy" if lang == LANG_EN else "Βαθμός: 1=ξανά 2=δύσκολο 3=καλό 4=εύκολο", default="3").strip()
        ease = float(card.get("ease", 2.5))
        interval = int(card.get("interval", 1))
        card["seen"] = int(card.get("seen", 0)) + 1
        if rating == "1":
            interval = 1
            ease = max(1.3, ease - 0.2)
        elif rating == "2":
            interval = max(1, int(interval * 1.2))
            ease = max(1.3, ease - 0.05)
        elif rating == "4":
            interval = max(2, int(interval * (ease + 0.5)))
            ease = min(3.0, ease + 0.1)
        else:
            interval = max(2, int(interval * ease))
        due_ts = time.time() + interval * 86400
        card["due"] = time.strftime("%Y-%m-%d", time.localtime(due_ts))
        card["interval"] = interval
        card["ease"] = round(ease, 2)

    srs["python"] = cards
    save_srs(srs)
    cprint(tr("saved", lang))


