def show_python_lesson(p: Progress, lesson: Lesson) -> None:
    lang = p.ui_lang
    title = lesson.title_en if lang == LANG_EN else lesson.title_el
    chap = lesson.chapter_en if lang == LANG_EN else lesson.chapter_el
    body = lesson.body_en if lang == LANG_EN else lesson.body_el

    # Guided section view for special lessons
    sections = split_guided_sections(body)
    if sections:
        cprint()
        cprint(tr("guided_sections", lang) + ": " + str(len(sections)))
        cprint("1) " + tr("show_all", lang))
        cprint("2) " + tr("pick_section", lang))
        choice = prompt(tr("select", lang), default="1").strip()
        if choice == "2":
            for i, (t, _) in enumerate(sections, 1):
                cprint(str(i) + ") " + t)
            ssel = prompt(tr("section", lang), default="1").strip()
            try:
                idx = int(ssel) - 1
            except Exception:
                idx = 0
            idx = max(0, min(idx, len(sections) - 1))
            t, c = sections[idx]
            body = t + "\n" + ("-" * len(t)) + "\n" + c
        else:
            parts = []
            for t, c in sections:
                parts.append(t + "\n" + ("-" * len(t)) + "\n" + c)
            body = "\n\n".join(parts)

    cprint()
    if RICH_AVAILABLE:
        from rich.panel import Panel
        hdr = "[bold]" + title + "[/bold]\n[dim]" + chap + " • " + lesson.level + " • " + tr("lesson_id", lang) + " " + str(lesson.id) + "[/dim]\n\n[dim]" + tr("made_by", lang) + "[/dim]"
        cprint(Panel.fit(hdr))
        cprint(body)
    else:
        cprint(title + " — " + chap + " — " + lesson.level + " — " + tr("lesson_id", lang) + " " + str(lesson.id))
        cprint(tr("made_by", lang))
        cprint("-" * 70)
        cprint(body)

    _show_examples_python(lang, lesson.examples, safe_mode=p.safe_mode)

    cprint()
    cprint("Exercise:" if lang == LANG_EN else "Άσκηση:")
    cprint(lesson.exercise.prompt_en if lang == LANG_EN else lesson.exercise.prompt_el)

