def srs_session(p: Progress) -> None:
    lang = p.ui_lang
    srs = load_srs()
    cards = srs.get("python", [])
    if not cards:
        cprint("No cards yet." if lang == LANG_EN else "Δεν υπάρχουν κάρτες ακόμα.")
        return
    today = today_str()
    due = [c for c in cards if c.get("due", today) <= today]
    if not due:
        cprint("No cards due today. Great!" if lang == LANG_EN else "Καμία κάρτα για σήμερα. Τέλεια!")
        return
    random.shuffle(due)
    cprint()
    cprint(tr("srs", lang) + " — Python — " + tr("made_by", lang))
    for card in due[:40]:
        input("Q: " + card["q"] + "\n(Enter)")
        cprint("A: " + card["a"])
        rating = prompt("Rate: 1=again 2=hard 3=good 4=easy" if lang == LANG_EN else "Βαθμός: 1=ξανά 2=δύσκολο 3=καλό 4=εύκολο", default="3").strip()
        ease = float(card.get("ease", 2.5))
        interval = int(card.get("interval", 1))
        card["seen"] = int(card.get("seen", 0)) + 1
        if rating == "1":
            interval = 1
            ease = max(1.3, ease - 0.2)
        elif rating == "2":
            interval = max(1, int(interval * 1.2))
            ease = max(1.3, ease - 0.05)
        elif rating == "4":
            interval = max(2, int(interval * (ease + 0.5)))
            ease = min(3.0, ease + 0.1)
        else:
            interval = max(2, int(interval * ease))
        due_ts = time.time() + interval * 86400
        card["due"] = time.strftime("%Y-%m-%d", time.localtime(due_ts))
        card["interval"] = interval
        card["ease"] = round(ease, 2)
    srs["python"] = cards
    save_srs(srs)
    cprint(tr("saved", lang))

