def _workshop_exists(course: str, project_id: str) -> bool:
    return course == "python" and project_id in PY_WORKSHOP

def _generate_project(course: str, project_id: str) -> Optional[Path]:
    if course != "python":
        return None
    if project_id not in PY_WORKSHOP:
        return None

    _ensure_projects_root()
    root = PROJECTS_ROOT / project_id
    root.mkdir(parents=True, exist_ok=True)

    d = PY_WORKSHOP[project_id]
    _write_text(root / "README_EN.txt", d.get("readme_en", ""))
    _write_text(root / "README_EL.txt", d.get("readme_el", ""))

    for name, body in d.get("files", {}).items():
        _write_text(root / name, body)

    if d.get("tests"):
        _write_text(root / "test_project.py", d["tests"])

    return root

def _run_tests_in(root: Path) -> Tuple[bool, str]:
    try:
        proc = subprocess.run([sys.executable, "-m", "unittest", "-q"], cwd=str(root), capture_output=True, text=True, timeout=10)
        out = (proc.stdout or "") + (proc.stderr or "")
        return proc.returncode == 0, out.strip()
    except Exception as e:
        return False, str(e)

# Override show_project to include Workshop
def _show_project_v5(p: Progress, course: str, pr: Project) -> None:
    lang = p.ui_lang
    title = pr.title_en if lang == LANG_EN else pr.title_el
    goal = pr.goal_en if lang == LANG_EN else pr.goal_el
    steps = pr.steps_en if lang == LANG_EN else pr.steps_el
    stretch = pr.stretch_en if lang == LANG_EN else pr.stretch_el

    cprint()
    if RICH_AVAILABLE:
        from rich.panel import Panel
        hdr = "[bold]" + title + "[/bold]\n[dim]" + course.upper() + " • " + pr.difficulty + " • " + pr.id + "[/dim]\n\n" + goal + "\n\n[dim]" + tr("made_by", lang) + " • " + tr("version", lang) + ": " + APP_VERSION + "[/dim]"
        cprint(Panel.fit(hdr))
    else:
        cprint(title + " — " + course.upper() + " — " + pr.difficulty + " — " + pr.id)
        cprint(goal)
        cprint(tr("made_by", lang) + " • " + tr("version", lang) + ": " + APP_VERSION)

    cprint("\n" + ("Steps:" if lang == LANG_EN else "Βήματα:"))
    for i, s in enumerate(steps, 1):
        cprint(str(i) + ". " + s)

    cprint("\n" + ("Stretch goals:" if lang == LANG_EN else "Extra (stretch):"))
    for s in stretch:
        cprint("- " + s)

    if _workshop_exists(course, pr.id):
        cprint()
        cprint(tr("workshop_available", lang))
        if prompt(tr("workshop_generate", lang), default="y").lower().startswith("y"):
            root = _generate_project(course, pr.id)
            if root:
                d = PY_WORKSHOP[pr.id]
                cprint(tr("workshop_created", lang) + ": " + str(root))
                cprint("\n" + tr("workshop_files", lang) + ":")
                for fn in sorted(d.get("files", {}).keys()):
                    cprint("- " + fn)
                if d.get("tests"):
                    cprint("- test_project.py")
                cprint("\n" + tr("workshop_run", lang) + ": " + (d.get("run_en") if lang == LANG_EN else d.get("run_el")))
                cprint("\n" + tr("workshop_next", lang) + ":")
                if lang == LANG_EN:
                    cprint("1) cd " + str(root))
                    cprint("2) Read README_EN.txt")
                    cprint("3) Run the app")
                    cprint("4) Change one thing and test again")
                else:
                    cprint("1) cd " + str(root))
                    cprint("2) Διάβασε README_EL.txt")
                    cprint("3) Τρέξε την εφαρμογή")
                    cprint("4) Άλλαξε κάτι και δοκίμασε ξανά")

                if d.get("tests") and prompt(tr("workshop_tests", lang), default="y").lower().startswith("y"):
                    ok, out = _run_tests_in(root)
                    if ok:
                        cprint("✅ " + ("All tests passed." if lang == LANG_EN else "Όλα τα tests πέρασαν."))
                    else:
                        cprint("❌ " + ("Tests failed:" if lang == LANG_EN else "Αποτυχία tests:"))
                        cprint(out if out else "(no output)")

    if prompt(tr("workshop_done", lang), default="n").lower().startswith("y"):
        mark_project_done(p, course, pr.id)
        cprint(tr("saved", lang))

